function retrieveCaps(e){let l=e.text(),a=l.match(/[A-Z]/g);if(chars=[],replacement={},null!==a){for(let e=0;e<a.length;e++)chars.push('<span class="capital">'+a[e]+"</span>");for(let e=0;e<a.length;e++)replacement[a[e]]=chars[e];e.html(allReplace(l,replacement))}}function allReplace(e,l){for(let a in l)e=e.replace(new RegExp(a,"g"),l[a]);return e}